(22, 1, '2017-12-25 22:26:01', '2017-12-25 22:26:01', '<p style=\"text-align: justify;\"><span style=\"color: #ff0000;\"><strong>Curso Preparatório para Prova de Residência e Especialização em CTBMF</strong></span></p>\r\n\r\n\r\n<hr />\r\n\r\n<span style=\"color: #3366ff;\"><strong>Módulo I - Trauma (Parte I)</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li class=\"\">ATLS - Suporte básico de vida em trauma I</li>\r\n 	<li class=\"\">ATLS - Suporte básico de vida em trauma II</li>\r\n 	<li class=\"\">Tratamento das fraturas mandibulares</li>\r\n 	<li>Princípios de fixação interna aplicadas ao tratamento do trauma bucomaxilofacial</li>\r\n 	<li>Maxila: classificação das fraturas maxilares</li>\r\n 	<li>Zigoma: Diagnóstico das fraturas zigomático-orbitária</li>\r\n</ul>\r\n<strong><span style=\"color: #3366ff;\">Módulo II - Trauma (Parte II)</span></strong>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Órbita I: anatomia aplicada ao tratamento cirúrgico das fraturas orbitárias</li>\r\n 	<li>Órbita II: classificação e sequência de tratamento das fraturas zigomáticas</li>\r\n 	<li>NOE</li>\r\n 	<li>Frontal</li>\r\n 	<li>Noções de balística aplicadas as lesões traumáticas da face</li>\r\n 	<li>Lesões por PAF: diagnóstico e tratamento</li>\r\n 	<li>Traumatismo dentoalveolar - Parte I</li>\r\n 	<li>Traumatismo dentoalveolar - Parte II</li>\r\n 	<li>Fraturas Panfaciais</li>\r\n</ul>\r\n<span style=\"color: #3366ff;\"><strong>Módulo III - Anatomia</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li class=\"\" style=\"text-align: justify;\">Anatomia de Cabeça e Pescoço - Inervação</li>\r\n 	<li class=\"\" style=\"text-align: justify;\">Anatomia de Cabeça e Pescoço - Irrigação Arterial</li>\r\n 	<li class=\"\" style=\"text-align: justify;\">Anatomia de Cabeça e Pescoço - Drenagem Venosa e Linfática</li>\r\n 	<li class=\"\" style=\"text-align: justify;\">Anatomia da ATM</li>\r\n 	<li class=\"\" style=\"text-align: justify;\">Anatomia Músculo-Esquelética da Face</li>\r\n</ul>\r\n<span style=\"color: #3366ff;\"><strong>Módulo IV - Anestesiologia</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li class=\"\" style=\"text-align: justify;\">Farmacologia dos Anestésicos Locais e Complicações Sistêmicas em Anestesiologia</li>\r\n 	<li class=\"\" style=\"text-align: justify;\">Técnicas Anestésicas e Complicações Locorregionais em Anestesiologia</li>\r\n 	<li class=\"\" style=\"text-align: justify;\">Farmacologia dos Vasoconstrictores</li>\r\n</ul>\r\n<strong><span style=\"color: #3366ff;\">Módulo V - Infecções Odontogênicas</span></strong>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Infecções Odontogênicas: Etiologia e Diagnóstico</li>\r\n 	<li class=\"\">Infecções Odontogênicas: Classificação e Tratamento</li>\r\n</ul>\r\n<span style=\"color: #3366ff;\"><strong>Módulo VI - Cirurgia Buco-Dental</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Cirurgia dos dentes inclusos</li>\r\n 	<li>Cirurgias pré-protéticas I: tecidos moles</li>\r\n 	<li>Cirurgias pré-protéticas II: tecidos duros</li>\r\n 	<li>Emergências Médicas no Consultório <span style=\"color: #ff0000;\">(NOVO)</span></li>\r\n</ul>\r\n<strong><span style=\"color: #3366ff;\">Módulo VII - Ortognática</span></strong>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Técnica Cirúrgica I: tipos de osteotomias aplicada ao tratamento das deformidades</li>\r\n 	<li class=\"\">Técnica Cirúrgica II: previsibilidade e estabilidade</li>\r\n 	<li class=\"\">Diagnóstico e planejamento I</li>\r\n 	<li>Diagnóstico e planejamento II</li>\r\n 	<li>Apnéia Obstrutiva do Sono I</li>\r\n 	<li>Apnéia Obstrutiva do Sono II</li>\r\n</ul>\r\n<span style=\"color: #3366ff;\"><strong>Módulo VIII - Implantodontia</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li class=\"\">Classificação dos rebodos e dos tipos de implantes</li>\r\n 	<li class=\"\">Anatomia aplicada à implantodontia</li>\r\n 	<li class=\"\">Planejamento e etapas de reabilitação em implantodontia</li>\r\n</ul>\r\n<span style=\"color: #3366ff;\"><strong>Módulo IX - Terapêutica</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Antiinflamatórios e Analgésicos</li>\r\n 	<li>Antimicrobianos I</li>\r\n 	<li class=\"\">Antimicrobianos II</li>\r\n 	<li class=\"\">Atendimentos ao paciente com osteonecrose dos maxilares</li>\r\n 	<li>Atendimento ao paciente usuário de antiagregante plaquetário e anticoagulantes</li>\r\n 	<li>Antivirais e Antifúngicos aplicados em CTBMF</li>\r\n</ul>\r\n<strong><span style=\"color: #3366ff;\">Módulo X - ATM</span></strong>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Distúrbios de hipermobilidade I: etiopatogenia e tratamento conservador</li>\r\n 	<li class=\"\">Distúrbios de hipermobilidade II: tratamento cirúrgico</li>\r\n 	<li class=\"\">Distúrbios de hipomobilidade I</li>\r\n 	<li>Distúrbios de hipomobilidade II</li>\r\n 	<li>Desarranjos internos da ATM I : anatomia, diagnóstico e tratamento não cirúrgico</li>\r\n 	<li>Desarranjos internos da ATM II : artrocentese e artroscopia</li>\r\n 	<li>Desarranjos internos da ATM III : técnicas cirúrgicas abertas</li>\r\n</ul>\r\n<span style=\"color: #3366ff;\"><strong>Módulo XI - Patologia</strong></span>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Técnicas de ressecção e biópsia na região maxilofacial</li>\r\n 	<li class=\"\">Cistos odontogênico - PARTE I</li>\r\n 	<li class=\"\">Cistos odontogênico - PARTE II</li>\r\n 	<li>Tumores odontogênicos - Parte I</li>\r\n 	<li>Tumores odontogênicos - Parte II</li>\r\n 	<li>Lesões fibro-ósseas do complexo maxilofacial</li>\r\n 	<li>Síndromes do complexo maxilofacial I</li>\r\n 	<li>Síndromes do complexo maxilofacial II</li>\r\n</ul>\r\n<strong><span style=\"color: #3366ff;\">Módulo XII - Imaginologia</span></strong>\r\n\r\n<strong>Disciplinas</strong>\r\n<ul>\r\n 	<li>Técnicas de Radiografias Intra-Bucais</li>\r\n 	<li>Técnicas de Radiografias Extra-Bucais</li